<?phpnamespace App\Http\Controllers\Admin;use App\Flower;use App\Http\Controllers\Controller;use App\Order;use Illuminate\Http\Request;class flowersController extends Controller{    public function index()    {        $flowers = flower::paginate(10);        return view('admin.flowers.index', compact('flowers'));    }    public function show(flower $flower, Request $request)    {        $flower->delete();        return redirect()->back();    }    public function create(){        $flower = new flower();        return view('admin.flowers.create', compact('flower'));    }    public function store(Request $request){        $flower  = new flower();        $flower->name = $request->name;        $flower->value = $request->value;        $flower->save();        return redirect()->route('admin.flowers.index');    }}